use base64;
use std::fs;
use std::io::prelude::*;

fn main() {
    let text_in_base64 = "";

    let audio = base64::decode(text_in_base64).expect("Could not convert text in base 64 to audio");
    
    let mut file = fs::File::create("audio.mp3").expect("Could not create audio.mp3 file.");
    file.write_all(&audio).expect("Could not write data into audio file.");

    println!("Text to Audio converted successfully!");
}
